function switch_language(lang) {
  setCookie('language', lang, 2);
}
function setCookie(c_name, value, expiredays) {
  var exdate = new Date();
  exdate.setDate(exdate.getDate() + expiredays);
  document.cookie = c_name + "=" + escape(value) + ((expiredays == null) ? "" : ";expires=" + exdate.toGMTString()) + ";path=/"
}

var WebGlobalInitTracker = function (guidCookieName, baseDomain, cookiePath) {
  if (guidCookieName) {
    var expire = 365 * 5;
    var guid = this.getCookie(guidCookieName);
    if (null == guid) {
      guid = this.createGuid();
      this.setCookie(guidCookieName, guid, expire, cookiePath, baseDomain);
    } else {
      this.setCookie(guidCookieName, guid, expire + 1, cookiePath, baseDomain);
    }
    /*var sessionCookieName = 'sessid';
    var sessionId = this.getCookie(sessionCookieName);
    if (null == sessionId) {
      sessionId = this.createGuid();
      this.setCookie(sessionCookieName, sessionId, 0, cookiePath, baseDomain);
    }
    this.setCookie('isp', 'true', expire, cookiePath, baseDomain);*/
  }
};

WebGlobalInitTracker.prototype.createGuid = function () {
  return (this.S4() + this.S4() + "-" + this.S4() + "-" + this.S4() + "-" + this.S4() + "-" + this.S4() + this.S4() + this.S4());
};

WebGlobalInitTracker.prototype.S4 = function () {
  return (((1 + Math.random()) * 0x10000) | 0).toString(16).substring(1).toUpperCase();
};

WebGlobalInitTracker.prototype.getCookie = function (checkName) {
  var a_all_cookies = document.cookie.split(';');
  var a_temp_cookie = '';
  var cookieName = '';
  var cookie_value = '';
  var b_cookie_found = false;
  for (i = 0; i < a_all_cookies.length; i++) {
    a_temp_cookie = a_all_cookies[i].split('=');
    cookieName = a_temp_cookie[0].replace(/^\s+|\s+$/g, '');
    if (cookieName == checkName) {
      b_cookie_found = true;
      if (a_temp_cookie.length > 1) {
        cookie_value = unescape(a_temp_cookie[1].replace(/^\s+|\s+$/g, ''));
      }
      return cookie_value;
    }
    a_temp_cookie = null;
    cookieName = '';
  }
  if (!b_cookie_found) {
    return null;
  }
};

WebGlobalInitTracker.prototype.setCookie = function (name, value, expires, path, domain, secure) {
  var today = new Date();
  today.setTime(today.getTime());
  if (expires) {
    expires = expires * 1000 * 60 * 60 * 24;
  }
  var expires_date = new Date(today.getTime() + (expires));
  document.cookie = name + "=" + escape(value) +
    ((expires) ? ";expires=" + expires_date.toGMTString() : "") +
    ((path) ? ";path=" + path : "") +
    ((domain) ? ";domain=" + domain : "") +
    ((secure) ? ";secure" : "");
};

new WebGlobalInitTracker('guid', '', '/');
