(function($, Drupal, window, document) {
    'use strict';
    Drupal.behaviors.common = {
        attach: function(context, settings) {

            $('#keyword_input').on('keyup', function() {
                if ($(this).val() != '') {
                    $('.searchBox').removeClass('error');
                }
            });

            $(".captcha img").on("click", function() {
                $(".reload-captcha-wrapper a").trigger("click");
            });

            var path = window.location.href;
            if (path.indexOf('admin/gr-admin') > 0) {
                $(".icon-1367").parent().addClass('active');
            } else if (path.indexOf('admin/data-management') > 0) {
                $(".icon-1679").parent().addClass('active');
            }

            if ($("body").hasClass('page-admin-structure-taxonomy-regulation-taxonomy') || $("body").hasClass('page-admin-structure-taxonomy-third-parties-category')) {
                var num = path.indexOf("&");
                var lan = path.substr(num + 5);
                if (lan != null && lan != undefined) {
                    $("#edit-language").find("option").each(function(i, element) {
                        if ($(this).val() == lan) {
                            $(this).attr("selected", "selected");
                        }
                    });
                }
            }

            $('.bdsharebuttonbox').on('click', function(e) {
                $("#qrcode").html('');
                $("#qrcode").qrcode(window.location.href);
                $("#qrcode").show();
                $(document).one("click", function() {
                    $("#qrcode").hide();
                });
                e.stopPropagation();
            });
        }
    };
})(jQuery, Drupal, this, this.document);

